//webkitURL is deprecated but nevertheless
URL = window.URL || window.webkitURL;

var gumStream; 						//stream from getUserMedia()
var rec; 							//Recorder.js object
var input; 							//MediaStreamAudioSourceNode we'll be recording

// shim for AudioContext when it's not avb. 
var AudioContext = window.AudioContext || window.webkitAudioContext;
var audioContext //audio context to help us record

var recordButton = document.getElementById("recordButton");
var stopButton = document.getElementById("stopButton");
var alarmAfter = document.getElementById("alarmAfter");

//add events to those 2 buttons
stopButton.disabled = true;
recordButton.addEventListener("click", startRecording);
stopButton.addEventListener("click", stopRecording);

var alarmAudio = new Audio('alarm.wav');
alarmAudio.addEventListener('ended', function(){
    this.currentTime = 0;
    this.play();
}, false)

var analyzer;

if (typeof Meyda === "undefined") {
    console.log("Meyda could not be found! Have you included it?");
}

var isWhistling = false;
var numWhistles = 0;
var whistlingStartedAt = Date.now();
var alertAfter = Infinity;

function startRecording() {
    console.log("recordButton clicked");
    console.log(alarmAfter.value)
    const parsed = parseInt(alarmAfter.value, 10);
    if (!isNaN(parsed) & parsed > 0) {
        alertAfter = parsed;
    } else {
        alert("Enter positive integer for alert after")
        return;
    }
    alarmAfter.disabled = true;

	/*
		Simple constraints object, for more advanced audio features see
		https://addpipe.com/blog/audio-constraints-getusermedia/
	*/
    
    var constraints = { audio: true, video:false }

 	/*
    	Disable the record button until we get a success or fail from getUserMedia() 
	*/

	recordButton.disabled = true;
	stopButton.disabled = false;

	/*
    	We're using the standard promise based getUserMedia() 
    	https://developer.mozilla.org/en-US/docs/Web/API/MediaDevices/getUserMedia
	*/

	navigator.mediaDevices.getUserMedia(constraints).then(function(stream) {
		console.log("getUserMedia() success, stream created, initializing Recorder.js ...");

		/*
			create an audio context after getUserMedia is called
			sampleRate might change after getUserMedia is called, like it does on macOS when recording through AirPods
			the sampleRate defaults to the one set in your OS for your playback device
		*/
        audioContext = new AudioContext();

		/*  assign to gumStream for later use  */
		gumStream = stream;
		/* use the stream */
        input = audioContext.createMediaStreamSource(stream);

        isWhistling = false;
        numWhistles = 0;
        whistlingStartedAt = Date.now()

        // Create the Meyda Analyzer
        analyzer = Meyda.createMeydaAnalyzer({
            // Pass in the AudioContext so that Meyda knows which AudioContext Box to work with
            "audioContext": audioContext,
            // Source is the audio node that is playing your audio. It could be any node,
            // but in this case, it's the MediaElementSourceNode corresponding to your
            // HTML 5 Audio Element with your audio in it.
            "source": input,
            // Buffer Size tells Meyda how often to check the audio feature, and is
            // measured in Audio Samples. Usually there are 44100 Audio Samples in 1
            // second, which means in this case Meyda will calculate the level about 86
            // (44100/512) times per second.
            "bufferSize": 2048,
            // Here we're telling Meyda which audio features to calculate. While Meyda can
            // calculate a variety of audio features, in this case we only want to know
            // the "rms" (root mean square) of the audio signal, which corresponds to its
            // level
            "featureExtractors": ["zcr", "rms"],
            // Finally, we provide a function which Meyda will call every time it
            // calculates a new level. This function will be called around 86 times per
            // second.
            "callback": features => {
                console.log(features, numWhistles, isWhistling);
                if (features.zcr > 500 & features.rms> 0.05){
                    if (!isWhistling & Date.now() - whistlingStartedAt > 5000 ) {
                        whistlingStartedAt = Date.now();
                        numWhistles += 1;
                        document.getElementById("numWhistles").innerHTML = numWhistles;
                        if (numWhistles >= alertAfter) {
                            alarmAudio.play();
                        }
                    }
                    isWhistling = true;
                } else {
                    isWhistling = false;
                }
            }
        });
        analyzer.start();
        
        // var analyser = audioContext.createAnalyser();
        // var dataArray = new Float32Array(analyser.fftSize); // Float32Array needs to be the same length as the fftSize 
        // analyser.getFloatTimeDomainData(dataArray); // fill the Float32Array with data returned from getFloatTimeDomainData()

        // input.connect(analyser)
        // analyser.connect(audioContext.destination)

        // start = null;
        // function step(timestamp) {
        //     if (stopButton.disabled) return;
        //     if (!start) {
        //         start = timestamp;
        //         analyser.getFloatTimeDomainData(dataArray);
        //         console.log(dataArray);
        //     }
        //     var progress = timestamp - start;
        //     if (progress < 1000) {
        //         window.requestAnimationFrame(step);
        //     } else {
        //         start = null;
        //         window.requestAnimationFrame(step);
        //     }
        // }
        
        // window.requestAnimationFrame(step);

	}).catch(function(err) {
	  	//enable the record button if getUserMedia() fails
    	recordButton.disabled = false;
    	stopButton.disabled = true;
	});
}

function stopRecording() {
	console.log("stopButton clicked");

    //disable the stop button, enable the record too allow for new recordings
    alarmAudio.pause();
	stopButton.disabled = true;
    recordButton.disabled = false;
    alarmAfter.disabled = false;
    alertAfter = Infinity;
    document.getElementById("numWhistles").innerHTML = 0;

	//stop microphone access
    gumStream.getAudioTracks()[0].stop();
    isWhistling = false;
    numWhistles = 0;
    analyzer.stop();
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}


